import React, { useState } from 'react'

const ContactComponent = ({ contact }) => {
    const { name, status, online } = contact;


    function onLineColor(online) {
        return online ? '#58D68D' : '#ABB2B9';
    }

    function isOnline(online) {
        return online ? '(onLine)' : '(offLine)';
    }

    return (
        <div className="card m-4" style={{ width: '20rem'}}>

            <div className='card-header d-flex justify-content-between bg-danger'>

                <div className='d-flex align-items-center gap-2 bg-success d-flex align-items-center'>
                    <h5 className='bg-primary'>{name}</h5>
                    <p style={{ fontSize: '11px' }}>{isOnline(online)}</p>
                </div>

                <div style={{ width: '20px', height: '20px', backgroundColor: onLineColor(online), borderRadius: '50%' }}></div>
            </div>

            <div className='card-body'>
                <p className='text-center text-success'>
                    {status}
                </p>
            </div>

        </div>
    )
}

export default ContactComponent
