/*Este formulario me permitira trabajar con los datos para crear una nueva tarea para tasklist*/
import React, { useRef } from 'react'
import PropTypes from 'prop-types';
import { Contact } from '../../../models/Contact.class';


const ContactForm = ({ add }) => {

  const nameRef = useRef('')
  const statusRef = useRef('')

  function addContact(e) {
    e.preventDefault();
    const newContact = new Contact(
      nameRef.current.value,
      statusRef.current.value,
      false
    )
    add(newContact);
  }

  return (
    <form onSubmit={addContact} className='py-5 px-2 bg-secondary mt-5 mx-4'>
       <h1>Contact Fomr</h1>
      <div className='form-outline flex-fill d-flex flex-column gap-2'>
            <input type="text" placeholder='Name' />
            <input type="text"  placeholder='Status'/>
      </div>
      <button type='submit' className='btn btn-sm btn-outline-secondary my-2'> Add Contact</button>
    </form>
  )
}

ContactForm.protoTypes = {
  add: PropTypes.func.isRequired
}

export default ContactForm;
