import React, { useState } from 'react'

const ContactComponent = ({ contact }) => {
    const { name, status, online } = contact;

    const [statusColor, setstatusColor] = useState(online)

    function onLineColor(online){
       return online ? 'success':'secondary'; 
    }

    return (
        <div class="card m-4" style={{ width: '20rem'}}>
            <h6 className='card-header' style={{backgroundColor: statusColor, color:'black'}}>{name}</h6>
            <div className='card-body'>
                <p className='text-center text-success'>
                    {status}
                </p>
            </div>
        </div>
    )
}

export default ContactComponent
