import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import '../../styles/task.scss'
import { Contact } from '../../models/Contact.class';


const ContactComponent = ({ contact, onLine }) => {

    const{name, status} = contact;

    useEffect(() => {
        console.log(`${name} it's in your contacts`);
    }, [contact])


    /**
   *Function that returns a icon depending if the task is completed or not
   */
    function statusChange() {
        if (status) {
            return (<i onClick={()=> onLine(status)} className='bi-toggle-on task-action' style={{ color: 'green' }}></i>)
        } else {
            return (<i onClick={()=> onLine(status)} className='bi-toggle-off task-action' style={{ color: 'grey' }}></i>)
        }
    }

    return (
        <tr className='fw-normal'>
            <th>
                <span className='ms-2'>
                    {name}
                </span>
            </th>
            <td>
                {statusChange()}
            </td>

            <td className='align-middle'>
                <i className='bi-trash task-action' onClick={()=> remove(contact)} style={{ color: 'tomato' }}></i>
            </td>
        </tr>

    );
};


ContactComponent.propTypes = {
    contact: PropTypes.instanceOf(Contact),
};

export default ContactComponent;
