import React, { useEffect, useState } from 'react';
import { Contact } from '../../models/Contact.class';
import ContactComponent from '../pure/ContatComponent';

const ContactList = () => {

    const defaultContact = new Contact('Ismael','offline',false);
    const [contacts, setContacts] = useState([defaultContact]);

    return (
        <div className='d-flex justify-content-center my-4'>
            <div className='col-10 bg-primary'>

                {/* CARD */}
                <div className='card'>

                    {/* Card Header */}
                    <div className='card-header p-4'>
                        <h5 className='d-flex justify-content-center'>
                          YOUR FRIENDS
                        </h5>
                    </div>

                    {/* Card Body */}
                    {contacts.map((contact, index)=>{
                        <div>
                            <ContactComponent person={contact} key={index}/>
                        </div>
                    })}                
                </div>
            </div>
        </div>
    );
};
export default ContactList;
