import React, { useState } from 'react';
import { Contact } from '../../models/Contact.class';
import ContactComponent from '../pure/ContactComponent';

const ContactList = () => {

    const defaultContact = new Contact('Ismael','Life its better with Code and Smoothie',true);
    const defaultContact1 = new Contact('Ismael','Life its better with Code and Smoothie',true);
    const defaultContact2 = new Contact('Ismael','Life its better with Code and Smoothie',true);

    const [contacts, setContacts] = useState([defaultContact,defaultContact1,defaultContact2]);

    return (
        <div className='d-flex justify-content-center my-4'>
            <div className='col-10 bg-primary'>

                {/* CARD */}
                <div className='card'>

                    {/* Card Header */}
                    <div className='card-header p-4'>
                        <h5 className='d-flex justify-content-center'>
                          YOUR FRIENDS
                        </h5>
                    </div>

                    {/* Card Body */}
                    {contacts.map((contact, index)=>{
                        return(
                            <ContactComponent contact={contact} key={index}/>
                        )
                    })}                
                </div>
            </div>
        </div>
    );
};
export default ContactList;
